  removeTags(s: string)
  {
    s = s.trim()+" ";
    while (s.search("<")!=-1)
    {
      var low = s.lastIndexOf("<");
      var high = s.lastIndexOf(">");
      var sub = s.substring(low, high+1);
      var temp;
      temp = s.replace(sub, "");
      s = temp.trim();
      high = s.lastIndexOf(">");
    }
    return s;
  }
  contains(array:number[], item:number) {
    if (array == null)
    {
      return false;
    }
    var i;
    for (i = 0; i < array.length; i++) {
      if (array[i] === item) {
        return true;
      }
    }
    return false;
  }

